function seedUsers(): void {
    for (let u of users) {
        insertUser(u);
    }
}

function seedEmployees(): void {
    for (let e of employees) {
        let employee = new Employee(e);
        employee.save().then(u => {
            console.log("Successfuly saved a user in db.");
        }).catch(err => {
            console.log("Error, couldn't save a user in db.");
        })
    }
}

function seedStudents(): void {
    for (let s of students) {
        let student = new Student(s);
        student.save().then(u => {
            console.log("Successfuly saved a user in db.");
        }).catch(err => {
            console.log("Error, couldn't save a user in db.");
        })
    }
}

function seedAllUsers(): void {
    const db = mongoose.connect('mongodb://localhost:27017/RTI_katedra');
    const connection = mongoose.connection;

    connection.db.dropCollection('users', function (err, result) { });
    connection.db.dropCollection('studentd', function (err, result) { });
    connection.db.dropCollection('employees', function (err, result) { });

    new Promise(resolve => {
        seedUsers();
        seedEmployees();
        seedStudents();
    }).then(async u => {
        (await db).disconnect();
    });
}

export { seedAllUsers };